package com.java.first;

public class Loops {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("for,while,do-while,for-each");
		
int i=1;//Intilization
while(i<=10) //condition
{
	System.out.println(2*i);
	i++; //Increment
}
	}

}
